import Countup from 'react-countup';
import { makeStyles } from '@material-ui/core';
import BGStaking from 'assets/svgs/BGStaking.svg';
import LETToken from 'assets/tokens/LET.svg';
import IconToken from 'assets/tokens/TokenWrapper.svg';
import BigNumber from 'bignumber.js';
import MediumButton from 'components/Buttons/MediumButton';
import { STONES } from 'utils/stones';

const useStyles = makeStyles({
  root: {
    width: 388,
    height: 294,
    marginLeft: 20,
    marginRight: 20,
    backgroundImage: `url(${BGStaking})`,
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    padding: 40,
  },
  mainText: {
    fontWeight: 700,
    color: '#FFD271',
  },
  subText: {
    fontFamily: 'Sen',
    color: '#CBA344',
  },
  summaryContainer: {
    width: '100%',
    display: 'flex',
    justifyContent: 'space-between',
  },
  tokenContainer: {
    display: 'flex',
    flexDirection: 'column',
  },
  imgContainer: {
    position: 'relative',
    width: 72,
    height: 40,
  },
  tokenImg: {
    backgroundImage: `url(${IconToken})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    marginLeft: 32,
    width: 40,
    height: 40,
    position: 'relative',
    '& img': {
      position: 'absolute',
      borderRadius: '50%',
      width: 20,
      height: 20,
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
    }
  },
  aprContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-end',
  },
  userStateContainer: {
  },
  rewardContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-end',
  },
  iconContainer: {
    width: 24,
    height: 24,
    marginRight: 12,
    position: 'relative',
    '& img': {
      position: 'absolute',
      left: '50%',
      top: '50%',
      transform: 'translate(-50%, -50%) scale(0.08)',
    },
  },
});

interface StakingInfoProps {
  isStaking?: boolean
  aprValue: number
  aprMaxValue?: number
  totalStaked: number
  balance: number
  staked: number
  disabled?: boolean
}

function InfoContainer(props: StakingInfoProps) {
  const classes = useStyles();
  const { isStaking, aprValue, aprMaxValue, totalStaked, balance, staked, disabled } = props;
  const owned = BigNumber.sum(balance, staked).toNumber();
  const USDC_DATA = 'data:image/png;base64,';

  return (
    <div className={classes.root}>
      <div className={classes.summaryContainer}>
        <div className={classes.tokenContainer}>
          <div className={classes.imgContainer}>
            {
              isStaking ?
                <img
                  src={LETToken}
                  style={{
                    position: 'absolute',
                    top: '50%',
                    left: 0,
                    transform: 'translate(0%, -50%)',
                  }}
                />
                :
                <>
                  <div className={classes.tokenImg}>
                    <img src={USDC_DATA} />
                  </div>
                  <img
                    src={LETToken}
                    style={{
                      position: 'absolute',
                      top: '50%',
                      left: '0%',
                      transform: 'translate(0%, -50%)',
                    }}
                  />
                </>
            }
          </div>
          <div className={classes.mainText} style={{ fontSize: 20, marginTop: 8, }}>
            {
              isStaking ?
                'stake LET' :
                'LET-USDC lp'
            }
          </div>
        </div>
        <div className={classes.aprContainer}>
          <div className={classes.subText} style={{ fontSize: 12, }}>APR</div>
          <div className={classes.mainText} style={{ fontSize: 16, marginTop: 4 }}>{
            aprMaxValue ?

              <>
                <Countup
                  start={0}
                  end={aprValue}
                  delay={0}
                  duration={0.75}
                  separator=","
                  decimals={2}
                  decimal="."
                  suffix="%"
                />
                {` ~ `}
                <Countup
                  start={0}
                  end={aprMaxValue}
                  delay={0}
                  duration={0.75}
                  separator=","
                  decimals={2}
                  decimal="."
                  suffix="%"
                />
              </>
              :
              <Countup
                start={0}
                end={aprValue}
                delay={0}
                duration={0.75}
                separator=","
                decimals={2}
                decimal="."
                suffix="%"
              />
          }</div>
          <div className={classes.subText} style={{ fontSize: 12, marginTop: 8 }}>{
            isStaking ?
              'Total staked' :
              'Total volume'
          }</div>
          <div className={classes.mainText} style={{ fontSize: 16, marginTop: 4 }}>{
            isStaking ?
              <Countup
                start={0}
                end={totalStaked}
                delay={0}
                duration={0.75}
                separator=","
                decimals={3}
                decimal="."
                suffix=" LET"
              /> :
              <Countup
                start={0}
                end={totalStaked}
                delay={0}
                duration={0.75}
                separator=","
                decimals={0}
                decimal="."
                prefix="$ "
                suffix="M"
              />
          }</div>
        </div>
      </div>
      <div className={classes.userStateContainer}>
        <div className={classes.subText} style={{ fontSize: 12, }}>You have</div>
        <div className={classes.mainText} style={{ fontSize: 16, marginTop: 8 }}>{
          isStaking ?
            <Countup
              start={0}
              end={owned}
              delay={0}
              duration={0.75}
              separator=","
              decimals={6}
              decimal="."
              suffix=" LET"
            /> :
            <Countup
              start={0}
              end={owned}
              delay={0}
              duration={0.75}
              separator=","
              decimals={6}
              decimal="."
              prefix=""
              suffix=" LP STAKED"
            />
        }</div>
        <div className={classes.mainText} style={{ fontSize: 16, marginTop: 8 }}>
          <Countup
            start={0}
            end={staked}
            delay={0}
            duration={0.75}
            separator=","
            decimals={6}
            decimal="."
            prefix="= "
            suffix=" STAKED"
          />
          <Countup
            start={0}
            end={balance}
            delay={0}
            duration={0.75}
            separator=","
            decimals={6}
            decimal="."
            prefix=" + "
            suffix=" STAKABLE"
          />
        </div>
        <div className={classes.mainText} style={{ fontSize: 16, marginTop: 8 }}></div>
      </div>
      <div className={classes.rewardContainer}>
        <div>
          <div className={classes.subText} style={{ marginBottom: 8 }}>Rewards</div>
          {!isStaking ?
            <div className={classes.iconContainer}>
              <img src={STONES.LET} />
            </div> :
            <div style={{ display: 'flex', }}>
              <div className={classes.iconContainer}>
                <img src={STONES.LET} />
              </div>
              <div className={classes.iconContainer}>
                <img src={STONES.BTC} />
              </div>
              <div className={classes.iconContainer}>
                <img src={STONES.ETH} />
              </div>
              <div className={classes.iconContainer}>
                <img src={STONES.SOL} />
              </div>
              <div className={classes.iconContainer}>
                <img src={STONES.USDC} />
              </div>
              <div className={classes.iconContainer}>
                <img src={STONES.USDT} />
              </div>
            </div>
          }
        </div>
        {isStaking ?
          <MediumButton text={disabled ? "Comming soon..." : "LETS stake"} link="/staking/stake" disabled={disabled}/> :
          <MediumButton text={disabled ? "Comming soon..." : "LETS provide lp"} link="/staking/pool" disabled={disabled}/>
        }
      </div>
    </div >
  );
}

export default InfoContainer;
